import React, { useState } from 'react';

function FraudDetection() {
  const [transactions, setTransactions] = useState('');
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResults(null);

    try {
      const data = JSON.parse(transactions);
      const response = await fetch('http://localhost:5000/api/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ transactions: data })
      });
      const result = await response.json();
      if (response.ok) {
        setResults(result);
      } else {
        setError(result.error || 'Prediction failed');
      }
    } catch (err) {
      setError('Invalid JSON format');
    }
    setLoading(false);
  };

  return (
    <div className="space-y-6">
      <h1 className="text-3xl font-bold">Detect Fraud</h1>
      <div className="bg-white p-6 rounded-lg shadow">
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Transaction Data (JSON)</label>
            <textarea
              value={transactions}
              onChange={(e) => setTransactions(e.target.value)}
              className="mt-1 block w-full h-48 rounded-md border-gray-300 shadow-sm"
              placeholder='{"transactions": [...]}'
            />
          </div>
          <button
            type="submit"
            disabled={loading}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 disabled:opacity-50"
          >
            {loading ? 'Processing...' : 'Detect Fraud'}
          </button>
        </form>

        {error && (
          <div className="mt-4 text-red-500">{error}</div>
        )}

        {results && (
          <div className="mt-4 p-4 bg-blue-50 rounded">
            <h3 className="text-lg font-medium text-blue-800">Results</h3>
            <div className="mt-2 space-y-2 text-sm text-blue-700">
              <p>Total Transactions: {results.total_transactions}</p>
              <p>Fraud Detected: {results.fraud_count}</p>
              <p>Fraud Percentage: {results.fraud_percentage.toFixed(2)}%</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default FraudDetection;
