import React, { useState } from 'react';
import axios from 'axios';

function ModelTraining() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError(null);
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Please select a file');
      return;
    }

    setLoading(true);
    const reader = new FileReader();
    reader.onload = async (e) => {
      try {
        const data = JSON.parse(e.target.result);
        const response = await fetch('http://localhost:5000/api/train', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ data })
        });
        const result = await response.json();
        if (response.ok) {
          setResult(result);
        } else {
          setError(result.error || 'Training failed');
        }
      } catch (err) {
        setError('Invalid file format or training failed');
      }
      setLoading(false);
    };
    reader.readAsText(file);
  };

  const trainFromCSV = async () => {
    try {
      const res = await axios.post('/api/train-from-csv');
      alert('Training complete! Metrics: ' + JSON.stringify(res.data.metrics));
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="space-y-6">
      <h1 className="text-3xl font-bold">Train Model</h1>
      <div className="bg-white p-6 rounded-lg shadow">
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Training Data</label>
            <input
              type="file"
              accept=".json,.csv"
              onChange={handleFileChange}
              className="mt-1 block w-full"
            />
          </div>
          <button
            type="submit"
            disabled={loading}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 disabled:opacity-50"
          >
            {loading ? 'Training...' : 'Train Model'}
          </button>
        </form>

        <button onClick={trainFromCSV} className="btn btn-primary">
          Train Model from CSV
        </button>

        {error && (
          <div className="mt-4 text-red-500">{error}</div>
        )}

        {result && (
          <div className="mt-4 p-4 bg-green-50 rounded">
            <h3 className="text-lg font-medium text-green-800">Training Successful</h3>
            <pre className="mt-2 text-sm text-green-700">
              {JSON.stringify(result.metrics, null, 2)}
            </pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default ModelTraining;
